sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var currentDateObj = new Date();   
	var currentDateStr = formatDate(currentDateObj, g_user_date_format);   
    var today = getDateFromFormat(currentDateStr, g_user_date_format);   
    var start_date = getDateFromFormat(newValue, g_user_date_format); 
	if(start_date>today){
		g_form.showFieldMsg('event_start_date','Start Date shoulld not be in the future','error');
		return;
	}
	var end_date = getDateFromFormat(g_form.getValue('event_end_date'), g_user_date_format); 
	if(start_date>end_date&&g_form.getValue('event_end_date')!=''){
		g_form.showFieldMsg('event_end_date','Start Date should not be after end date','error');
		return;
	}
	else
		g_form.hideFieldMsg('event_start_date');
}
